import { Component } from '@angular/core';

@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css']
})
export class TasksComponent {

  tasks = [
    { id: 1, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-pink.png", checkbox:true },

    { id: 2, title: "Study Well", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicin", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id: 3, title: "Clean yourself", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },
    { id: 4, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },


    { id: 5, title: "work hard ", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-green.png", checkbox: false },

    { id: 6, title: "be honest", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id: 7, title: "to write book", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },
    { id: 8, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },

    { id: 9, title: "my name", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id: 10, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-pink.png", checkbox: false },

    { id: 11, title: "Fetch Water", menu: "assets/more.png.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id: 12, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-green.png", checkbox: false },


  ]
  edit:any;
  editMenu(d:HTMLImageElement) {
    let id:any  = d.id
    console.log(id);
    this.tasks.forEach(e=>{
      if(e.id == id){
        return this.edit = id
      }
    })

  }

  displayValue: string = ''
  navValueSearch(d: string) {
    this.displayValue = d
    console.log(d);

  }

  taskValue: string = ''
  searchtaskValue(d: string) {
    this.taskValue = d;
  }

  radioValue: boolean = false;

  sendRadioValue(d:any) {
    let id = d.target.value;
    let select = d.target.checked; 

    this.tasks = this.tasks.map((e)=>{
       if(e.id ==  id){
        e.checkbox = select;
        return  e;
       }
       return e
    });
  console.log(this .tasks);

}

hidetask(d:boolean){
if(d){
   this.tasks = this.tasks.filter(task => task.checkbox !== true);
}else{
  this.tasks
}
}
