import { Component,Input } from '@angular/core';

@Component({
  selector: 'app-popup',
  templateUrl: './popup.component.html',
  styleUrls: ['./popup.component.css']
})
export class PopupComponent {


@Input() value:string = '';

closePop(){
  this.value = 'false'
  setTimeout(()=>{
    this.value = ''
  },1000)
}




}
