    { id:4, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },


    { id:5, title: "work hard ", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-green.png", checkbox: false },

    { id:6, title: "be honest", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

