    { id:8, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-red.png", checkbox: false },

    { id:9, title: "my name", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id:10, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Accusantium, rerum! Provident rerum velit reiciendis, architecto nesciunt minima. Amet, dolor nam.", type: "assets/icons8-filled-circle-pink.png", checkbox: false },

    { id:11, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-blue.png", checkbox: false },

    { id:12, title: "Fetch Water", menu: "assets/more.png", description: "Lorem ipsum, dolor sit amet consectetur adipisicing elit.", type: "assets/icons8-filled-circle-green.png", checkbox: false },

  ]


  deleteTask(d:HTMLParagraphElement){
    let id:any = d.id;
    for (let i = 0; i < this.tasks.length; i++) {
      if (this.tasks[i].id === (id-1)) {
        console.log(this.tasks[i].id);

       this.tasks.splice(i, 1); 
       console.log(i);
       // remove the element at index i
        break; // exit the loop once the element is removed
      }
    }
  }  

  edit:any;
  editMenu(d:HTMLImageElement) {
    let id:any  = d.id
    console.log(id);
    this.tasks.forEach(e=>{
      if(e.id == id){
        return this.edit = id
      }
    })
  }

 barValue:any;
  editbar(d:HTMLParagraphElement){
    let id :any = d.id
    this.barValue = id;
}

  displayValue: string = ''
  navValueSearch(d: string) {
    this.displayValue = d
    console.log(d);
}

  taskValue: string = ''
  searchtaskValue(d: string) {
    this.taskValue = d;
  }

radiovalue:boolean = false
  sendRadioValue(d:any) {
    let id = d.target.value;
    let select = d.target.checked; 
    console.log(select);
    this.tasks = this.tasks.map((e)=>{
       if(e.id ==  id){
        e.checkbox = select;
        return e
       }
       e.checkbox = this.radiovalue
       return e;
    });
  console.log(this .tasks);



}


hidetask(d:boolean){
if(d){
   this.tasks = this.tasks.filter(task => task.checkbox !== true);
}else{
  this.tasks
}
}

