import { Component,Input } from '@angular/core';

@Component({
  selector: 'app-popup',
  templateUrl: './popup.component.html',
  styleUrls: ['./popup.component.css']
})
export class PopupComponent {
  closeValue?:boolean;

closePop(){
    this.closeValue =  true
  }
@Input('data') value:boolean=false;

}
