import { Component, ElementRef, EventEmitter, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {



  nav =[
    {circle:"assets/icons8-filled-circle-red.png", h:"work"},
    {circle:"assets/icons8-filled-circle-green.png", h:"entertainment"},
    {circle:"assets/icons8-filled-circle-pink.png", h:"family"},
    {circle:"assets/icons8-filled-circle-blue.png", h:"study"}
  ]

 ngOnInit(){
 }
 @Output()
 navValue:EventEmitter<string> = new EventEmitter<string>();

 SendNavValue(d:HTMLImageElement){
  let data = d.src;
 this.navValue.emit(data.slice(22))
 console.log(data);

 }



}
<nav class="d-flex container bg-light row p-3"
style="position: sticky; left: 0px; top: 100px;">
<div class="d-flex  gap-2 my-1  mx-2 navmenu" *ngFor="let e of nav" (click)="SendNavValue(myVariable)">
    <img #myVariable class="" style="height:32px;" src={{e.circle}}>
    <h3  style="font-family:monospace;font-size:20px;" >{{e.h}}</h3>
</div>
<div>
    <input type="text" placeholder="Search Task">
</div>

<div class="d-flex gap-3 my-3 align-items-center">
    <input  #man type="checkbox" class="hide" value="hide">
    <label>Hide done tasks</label>

</div>
<!-- <div>
    <img class="img-fluid" src="assets/boy.png" style="height:300px;" >
</div> -->
</nav>
